  `coupon_id` int(11) NOT NULL AUTO_INCREMENT,
  `coupon_name` varchar(64) DEFAULT NULL,
  `coupon_code` varchar(64) DEFAULT NULL,
  `coupon_type` tinyint(1) NOT NULL COMMENT '0=Per Order, 1=Per Product',
